//        cout << x.first << " " << x.second << endl;
//    }

    if(s.begin()->first > (N-1)/2+1) {
        cout << "-1" << endl;
        return 0;
    }
    //cout << "out : " << N << endl;
    for(int res, pre=0; N--; pre=res) {
        //cout << N << endl;
        int temp = s.begin()->first;
        if(temp > (N-1)/2+1) {
            auto it = s.begin();
            if(it->second == pre)
                it++;
            res = -it->second;
            if(it->first==1)
                cnt.erase(res);
            else {
                s.insert({it->first-1, it->second});
                cnt[res]--;
            }
            s.erase(it);
        } else {
            auto it = cnt.begin();
            if(it->first==pre)
                it++;
            res = it->first;
            s.erase({it->second, -res});
            if(it->second==1)
                cnt.erase(it);
            else {
                it->second--;
                s.insert({it->second, -res});
            }
        }
        cout << res << ' ';
    }
    cout << endl;
    return 0;
}


