    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int T = in.nextInt();
        while(T-- > 0) {
            char[] list = in.next().toCharArray();
            int l = list.length;
            int cnt0, cnt1;
            int ans = 0x3f3f3f3f;
            for(int i=0; i<=l; ++i) {
